import Texto from 'C:/Users/Rodrigo/Desktop/OFTRANCE/app-of-trance/src/componentes/Texto';
import { StyleSheet, Image, Dimensions } from 'react-native';
import topo from 'C:/Users/Rodrigo/Desktop/OFTRANCE/app-of-trance/assets/topo2.png';

//constante pra receber o tamanho da imagem 
const width = Dimensions.get("screen").width;
const largura = 720;
const altura = 720;
const valor = altura / largura * width;

export default function Topo({ titulo }) {
    return <>
        <Image source={topo} style={estilos.topo} />
        <Texto style={estilos.titulo}>ॐ RAVESTORE</Texto>

        <Texto style={estilos.desc1}>Confecção à mão | Totalmente singular</Texto>
        <Texto style={estilos.desc1}>- Roupas a pronta entrega e encomendas</Texto>
        <Texto style={estilos.desc1}>- Aceitamos cartão |  boleto | TED</Texto>
        <Texto style={estilos.desc1}>Confecção à mão | Totalmente singular</Texto>
        <Texto style={estilos.desc1}>Confecção à mão | Totalmente singular</Texto>

        'Confecção à mão | Totalmente singular',
        desc2: '- Roupas a pronta entrega e encomendas',
        desc3: '- Aceitamos cartão |  boleto | TED',
        desc4: '- Entregas no metrô & correios' 

    </>;
}

const estilos = StyleSheet.create({
    topo: { //imagem do topo
        width: '100%',
        height: valor

    },
    titulo: { //titulo "ravestore"
        color: '#4B0082',
        fontSize: 30,
        fontWeight: 'bold',
        lineHeight: 60,
        textAlign: 'center',
        paddingTop: 18

    },
    desc1: { //descições da loja
        fontSize: 16,
        fontWeight: 'bold',
        textAlign: 'center',
        paddingBottom: 15,

    },
    desc2: { //descições da loja
        fontSize: 14,
