import React from "react";
import { StyleSheet, View, Image } from 'react-native';
import Texto from 'C:/Users/Rodrigo/Desktop/OFTRANCE/app-of-trance/src/componentes/Texto';

export default function Produtos({ titulo, lista }){
    return <>
    <Texto style={ estilos.titulo }>{ titulo }</Texto>
    {
        lista.map(( {nome, imagem}) => {
            return <View key={nome}>
                <Image source={ imagem } style={ estilos.imagemprod }/>
                <Texto>{ nome }</Texto>
            </View>
        })
    }
    </>
}

const estilos = StyleSheet.create({
    imagemprod:{
        width: 46,
        height: 46
    },
    titulo: { //titulo "ravestore"
        color: '#4B0082',
        fontSize: 30,
        fontWeight: 'bold',
        lineHeight: 60,
        textAlign: 'center',
        paddingTop: 18

    },
})
