import React from 'react';
import { StyleSheet, Text, Image, Dimensions, View } from 'react-native';
import topo from '../../assets/topo2.png';
import prod1 from '../../assets/prod1.png';
import Texto from 'C:/Users/Rodrigo/Desktop/OFTRANCE/app-of-trance/src/componentes/Texto.js';

const width = Dimensions.get("screen").width;
const largura = 720;
const altura = 720;
const valor = altura / largura * width;

export default function Colecao(){
    return <> 
        <Image source={topo} style={estilos.img_topo} />

        <View style={estilos.cabecalho}>
            <Image source={prod1} style={estilos.imgproduto}/>
            <Texto style={estilos.prod1}>Macacão curto <br/>com manga flare</Texto>
        </View>

        <View style={estilos.colecao}>
            <Texto style={estilos.titulo}>OF TRANCE</Texto>
            <Texto>- Roupas a pronta entrega e encomendas</Texto>
            <Texto>- Entregas no metrô & correios</Texto>
            <Texto style={estilos.preco}>R$100,00</Texto>
        </View>
    </>
}

const estilos = StyleSheet.create({
    prod1: {
        lineHeight: 20,
        marginLeft: 10,
        marginTop: 20,
        color: '#191970',

    },
    cabecalho: {
        flexDirection: 'row',

    },
    preco:{
        color: '#191970',
        fontWeight: 'bold',
        fontSize: 20,
        lineHeight: 60,

    },
    titulo: {
        color: 'blue',
        fontSize: 30,
        fontWeight: 'bold',
        lineHeight: 60,

    },
    colecao:{
        paddingVertical: 10, //padding de cima e baixo
        paddingHorizontal: 20, //padding dos lados

    },
    imgproduto: {
        width: 90,
        height: 90,
        paddingTop: 10

    },
    img_topo: {
        width: '100%',
        height: valor

    },
});
