import React from "react";
import { StyleSheet, Image, Dimensions, Animated } from 'react-native';
import topo from 'C:/Users/Rodrigo/Desktop/OFTRANCE/app-of-trance/assets/topo2.png';

//constante pra receber o tamanho da imagem 
const width = Dimensions.get("screen").width;
const largura = 720;
const altura = 480;
const valor = altura / largura * width;

export default function Topo() {
    return <>
    <Animated.View>
        <Image source={topo} style={estilos.topo} />
        </Animated.View>
    </>;
}

const estilos = StyleSheet.create({
    topo: { //imagem do topo
        width: '100%',
        height: valor,
        borderBottomWidth: 6,
        borderBottomColor: '#3B0066'
    },
})
