import React from "react";
import Texto from 'C:/Users/Rodrigo/Desktop/OFTRANCE/app-of-trance/src/componentes/Texto';
import Texto2 from 'C:/Users/Rodrigo/Desktop/OFTRANCE/app-of-trance/src/componentes/Texto2';
import { StyleSheet, ScrollView, Image, Dimensions } from 'react-native';

const width = Dimensions.get("screen").width;
const largura = 720;
const altura = 520;
const valor = altura / largura * width;

export default function Detalhes({route}){
    const {promotion} = route.params
    return <>
        <ScrollView style={estilos.cabecalho} >

            <Image source={promotion.imgUrl} style={estilos.img}/>
            <Texto2 style={estilos.descc}>{promotion.title}</Texto2>
            <Texto2 style={estilos.preco}>APENAS R${promotion.price}</Texto2>
            
        </ScrollView> 
    </>
}

const estilos = StyleSheet.create({
    background: {
        paddingHorizontal: 20,
        marginVertical: 10,
    },
    img: {
        width: '100%',
        height: valor,
        marginVertical: 10,
        borderRadius: '20px'
    },
    descc: {
        fontSize: 18,
        fontWeight: 'bold',
        textAlign: 'center',
        paddingBottom: 10,
    },
    preco: {
        color: '#4B0082',
        fontSize: 20,
        fontWeight: 'bold',
        paddingVertical: 10,
        paddingBottom: 50,
    },
})
